mapper.insert:
insert into TB_BOARD (
	
) values (
	  TB_BAORD_SEQ.NEXTVAL
	, #{title}
	, #{content}
	, #{writer}
	, 0
	, NVL(#{noticeYn, jdbcType=VARCHAR}, 'N')
	, NVL(#{secretYn, jdbcType=VARCHAR}, 'N')
	, 'N'
	, SYSDATE
	, NULL
	, NULL
)

mapper.update:
UPDATE
	TB_BOARD
SET
	  UPDATE_TIME = SYSDATE
	, TITLE = #{title}
	, WRITER = #{writer}
	, CONTENT = #{content}
	, NOTICE_YN = NVL(#{notice_yn, jdbcType=VARCHAR}, 'N')
	, SECRET_YN = NVL(#{secret_yn, jdbcType=VARCHAR}, 'N')
WEHRE
	IDX = #{idx}
